'use strict';

var _index = require('./index');

describe('themer default colors', () => {

  it('should contain both a light and a dark theme', () => {
    expect(_index.colors.light).toBeDefined();
    expect(_index.colors.dark).toBeDefined();
  });

  it('should define all 16 required colors for each theme', () => {
    const prefixes = ['accent', 'shade'];
    prefixes.forEach(prefix => {
      for (let i = 0; i <= 7; i++) {
        expect(_index.colors.light[`${prefix}${i}`]).toBeDefined();
        expect(_index.colors.dark[`${prefix}${i}`]).toBeDefined();
      }
    });
  });
});